var res = 0

func numEnclaves(grid [][]int) int {
	res = 0

	m := len(grid)
	n := len(grid[0])
	// 淹掉靠边的陆地   【直接对每个元素 dfs 看是否是飞地，复杂度 M*N 比较高。】【只 dfs 靠边陆地元素，相当于最多只对 2*(M+N)-4 dfs】
	for i := 0; i < m; i++ {
		dfs(grid, i, 0)
		dfs(grid, i, n-1)
	}
	for j := 0; j < n; j++ {
		dfs(grid, 0, j)
		dfs(grid, m-1, j)
	}

	// 数一数剩下的 陆地面积(即单元格的数量)
	//【在另一个数 陆地块数 的解中，发现陆地就把相邻陆地都淹掉，是为了避免一个陆地由多个下标组成，导致数多了】
	for i := 0; i < m; i++ {
		for j := 0; j < n; j++ {
			if grid[i][j] == 1 {
				res += 1
			}
		}
	}

	return res
}

func dfs(grid [][]int, i, j int) {
	m := len(grid)
	n := len(grid[0])
	if i < 0 || j < 0 || i >= m || j >= n {
		return
	}
	if grid[i][j] == 0 {
		// 已经是海水了
		return
	}
	// 将 (i, j) 变成海水  直接用 grid 当 visited，减少维护 visited 数组
	grid[i][j] = 0
	// 淹没上下左右的陆地
	dfs(grid, i+1, j)
	dfs(grid, i, j+1)
	dfs(grid, i-1, j)
	dfs(grid, i, j-1)
	grid[i][j] = 1
}
